import React, { useState } from 'react';
import axios from 'axios';

function FeedbackForm() {
  const [formData, setFormData] = useState({
    employee_id: '',
    strengths: '',
    areas_to_improve: '',
    sentiment: 'positive',
  });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/feedback', formData);
      alert('✅ Feedback submitted successfully!');
      setFormData({
        employee_id: '',
        strengths: '',
        areas_to_improve: '',
        sentiment: 'positive',
      });
    } catch (err) {
      alert('❌ Error submitting feedback');
    }
  };

  return (
    <div className="w-full max-w-2xl bg-white rounded-2xl shadow-lg p-6 mx-auto mt-8">
      <h2 className="text-2xl font-semibold text-blue-800 mb-6 text-center">
        Submit Feedback
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Employee ID */}
        <div>
          <label className="block text-sm font-medium text-gray-600 mb-1">Employee ID</label>
          <input
            type="text"
            name="employee_id"
            value={formData.employee_id}
            onChange={handleChange}
            className="w-full border rounded-xl px-4 py-2 text-gray-700 focus:outline-none focus:ring focus:ring-blue-300"
            placeholder="Enter Employee ID"
            required
          />
        </div>

        {/* Strengths */}
        <div>
          <label className="block text-sm font-medium text-gray-600 mb-1">Strengths</label>
          <textarea
            name="strengths"
            value={formData.strengths}
            onChange={handleChange}
            rows="3"
            className="w-full border rounded-xl px-4 py-2 text-gray-700 resize-none focus:outline-none focus:ring focus:ring-blue-300"
            placeholder="What went well..."
            required
          />
        </div>

        {/* Areas to Improve */}
        <div>
          <label className="block text-sm font-medium text-gray-600 mb-1">Areas to Improve</label>
          <textarea
            name="areas_to_improve"
            value={formData.areas_to_improve}
            onChange={handleChange}
            rows="3"
            className="w-full border rounded-xl px-4 py-2 text-gray-700 resize-none focus:outline-none focus:ring focus:ring-blue-300"
            placeholder="What needs improvement..."
            required
          />
        </div>

        {/* Sentiment */}
        <div>
          <label className="block text-sm font-medium text-gray-600 mb-1">Overall Sentiment</label>
          <select
            name="sentiment"
            value={formData.sentiment}
            onChange={handleChange}
            className="w-full border rounded-xl px-4 py-2 text-gray-700 bg-white focus:outline-none focus:ring focus:ring-blue-300"
          >
            <option value="positive">Positive</option>
            <option value="neutral">Neutral</option>
            <option value="negative">Negative</option>
          </select>
        </div>

        {/* Submit Button */}
        <div className="text-center pt-4">
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-2 rounded-xl w-full sm:w-auto"
          >
            Submit Feedback
          </button>
        </div>
      </form>
    </div>
  );
}

export default FeedbackForm;
