import { useState } from 'react';
import FeedbackForm from '../Feedback/FeedbackForm';
import TeamMembers from '../Team/TeamMembers';
import FeedbackList from '../Feedback/FeedbackList';

export default function ManagerDashboard() {
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [activeTab, setActiveTab] = useState('team');

  return (
    <div className="space-y-6">
      <div className="flex border-b">
        <button
          className={`px-4 py-2 ${activeTab === 'team' ? 'border-b-2 border-blue-500' : ''}`}
          onClick={() => setActiveTab('team')}
        >
          My Team
        </button>
        <button
          className={`px-4 py-2 ${activeTab === 'feedback' ? 'border-b-2 border-blue-500' : ''}`}
          onClick={() => setActiveTab('feedback')}
        >
          Feedback History
        </button>
      </div>

      {activeTab === 'team' && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-1">
            <TeamMembers 
              onSelectEmployee={setSelectedEmployee} 
              selectedEmployee={selectedEmployee}
            />
          </div>
          <div className="md:col-span-2">
            {selectedEmployee ? (
              <FeedbackForm 
                employee={selectedEmployee} 
                onSubmit={(data) => {
                  // API call to submit feedback
                  console.log('Submitting feedback:', data);
                  setSelectedEmployee(null);
                }}
              />
            ) : (
              <div className="bg-white p-6 rounded-lg shadow-md text-center">
                <p className="text-gray-500">Select a team member to give feedback</p>
              </div>
            )}
          </div>
        </div>
      )}

      {activeTab === 'feedback' && (
        <FeedbackList 
          isManagerView={true}
          feedback={[]} // Replace with actual feedback data
        />
      )}
    </div>
  );
}
