import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  FiStar,
  FiThumbsUp,
  FiTrendingUp,
  FiClock,
  FiUser,
  FiMessageSquare,
  FiChevronRight,
  FiChevronLeft
} from 'react-icons/fi';
import SentimentIndicator from './SentimentIndicator';

const EmployeeDashboard = () => {
  const navigate = useNavigate();
  const [state, setState] = useState({
    feedbacks: [],
    loading: true,
    error: null,
    userInfo: null,
    stats: {
      totalFeedback: 0,
      avgRating: 0,
      positiveCount: 0,
      acknowledgedCount: 0
    },
    timeFilter: 'all' // 'all', 'month', 'quarter', 'year'
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userData = JSON.parse(localStorage.getItem('userData'));
        const response = await axios.get('http://localhost:8000/feedback/employee', {
          headers: { Authorization: `Bearer ${userData.token}` }
        });

        const feedbacks = response.data.map(f => ({
          ...f,
          created_at: new Date(f.created_at),
          is_acknowledged: Boolean(f.is_acknowledged)
        }));

        // Calculate statistics
        const total = feedbacks.length;
        const avgRating = total > 0 
          ? (feedbacks.reduce((sum, f) => sum + f.rating, 0) / total).toFixed(1)
          : 0;
        const positiveCount = feedbacks.filter(f => f.sentiment === 'positive').length;
        const acknowledgedCount = feedbacks.filter(f => f.is_acknowledged).length;

        setState({
          ...state,
          feedbacks,
          loading: false,
          userInfo: userData.user,
          stats: {
            totalFeedback: total,
            avgRating,
            positiveCount,
            acknowledgedCount
          }
        });
      } catch (err) {
        setState({
          ...state,
          error: err.response?.data?.detail || err.message,
          loading: false
        });
      }
    };

    fetchData();
  }, []);

  const filterFeedbacks = () => {
    const now = new Date();
    let cutoffDate;

    switch (state.timeFilter) {
      case 'month':
        cutoffDate = new Date(now.setMonth(now.getMonth() - 1));
        break;
      case 'quarter':
        cutoffDate = new Date(now.setMonth(now.getMonth() - 3));
        break;
      case 'year':
        cutoffDate = new Date(now.setFullYear(now.getFullYear() - 1));
        break;
      default:
        return state.feedbacks;
    }

    return state.feedbacks.filter(f => f.created_at >= cutoffDate);
  };

  const FeedbackCard = ({ feedback }) => (
    <div className={`bg-white rounded-xl shadow-md overflow-hidden mb-6 border-l-4 ${
      feedback.sentiment === 'positive' ? 'border-green-500' :
      feedback.sentiment === 'negative' ? 'border-red-500' : 'border-blue-500'
    }`}>
      <div className="p-6">
        <div className="flex justify-between items-start mb-4">
          <div>
            <h3 className="font-medium text-gray-800 flex items-center">
              <FiClock className="mr-2" />
              {feedback.created_at.toLocaleDateString('en-US', {
                year: 'numeric',
                month: 'long',
                day: 'numeric'
              })}
            </h3>
            <p className="text-sm text-gray-500">
              From {feedback.manager_name}
            </p>
            {feedback.is_acknowledged && (
              <span className="inline-block mt-1 px-2 py-1 text-xs bg-green-100 text-green-800 rounded-full">
                Acknowledged
              </span>
            )}
          </div>
          <div className="flex items-center">
            <SentimentIndicator sentiment={feedback.sentiment} />
            <div className="ml-2 flex items-center">
              <FiStar className="text-yellow-400 mr-1" />
              <span className="font-medium">{feedback.rating}/5</span>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div className="bg-green-50 rounded-lg p-4">
            <h4 className="font-medium text-green-800 flex items-center mb-2">
              <FiThumbsUp className="mr-2" />
              Strengths
            </h4>
            <p className="text-gray-700">{feedback.strengths}</p>
          </div>
          <div className="bg-blue-50 rounded-lg p-4">
            <h4 className="font-medium text-blue-800 flex items-center mb-2">
              <FiTrendingUp className="mr-2" />
              Areas for Improvement
            </h4>
            <p className="text-gray-700">{feedback.areas_to_improve}</p>
          </div>
        </div>

        {feedback.acknowledgment_comment && (
          <div className="mt-4 bg-gray-50 p-3 rounded-lg">
            <h4 className="font-medium text-gray-800 flex items-center mb-2">
              <FiMessageSquare className="mr-2" />
              Your Response
            </h4>
            <p className="text-gray-700">{feedback.acknowledgment_comment}</p>
          </div>
        )}
      </div>
    </div>
  );

  const StatCard = ({ icon, value, label }) => (
    <div className="bg-white rounded-xl shadow-md p-6 flex items-center">
      <div className="mr-4 p-3 rounded-full bg-indigo-100 text-indigo-600">
        {icon}
      </div>
      <div>
        <p className="text-2xl font-bold">{value}</p>
        <p className="text-gray-500">{label}</p>
      </div>
    </div>
  );

  if (state.loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
      </div>
    );
  }

  if (state.error) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-center p-6 max-w-md bg-white rounded-xl shadow-md">
          <div className="text-red-500 text-5xl mb-4">‚ö†Ô∏è</div>
          <h2 className="text-xl font-bold text-gray-800 mb-2">Error Loading Dashboard</h2>
          <p className="text-gray-600 mb-4">{state.error}</p>
          <button
            onClick={() => window.location.reload()}
            className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  const filteredFeedbacks = filterFeedbacks();

  return (
    <div className="min-h-screen bg-gray-50 p-4 md:p-8">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-8">
          <div className="mb-4 md:mb-0">
            <h1 className="text-2xl md:text-3xl font-bold text-gray-800">
              Welcome back, {state.userInfo?.name || 'Employee'}
            </h1>
            <p className="text-gray-500">Here's your feedback overview</p>
          </div>
          
          <div className="flex space-x-2 bg-white rounded-lg shadow-sm p-1">
            {['all', 'month', 'quarter', 'year'].map((filter) => (
              <button
                key={filter}
                onClick={() => setState({...state, timeFilter: filter})}
                className={`px-3 py-1 text-sm rounded-md ${
                  state.timeFilter === filter
                    ? 'bg-indigo-600 text-white'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                {filter.charAt(0).toUpperCase() + filter.slice(1)}
              </button>
            ))}
          </div>
        </div>

        {/* Stats Overview */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          <StatCard
            icon={<FiStar className="text-xl" />}
            value={state.stats.totalFeedback}
            label="Total Feedback"
          />
          <StatCard
            icon={<FiThumbsUp className="text-xl" />}
            value={state.stats.avgRating}
            label="Average Rating"
          />
          <StatCard
            icon={<FiTrendingUp className="text-xl" />}
            value={state.stats.positiveCount}
            label="Positive Feedback"
          />
          <StatCard
            icon={<FiMessageSquare className="text-xl" />}
            value={`${state.stats.acknowledgedCount}/${state.stats.totalFeedback}`}
            label="Acknowledged"
          />
        </div>

        {/* Feedback Timeline */}
        <div className="bg-white rounded-xl shadow-md p-6">
          <h2 className="text-xl font-bold text-gray-800 mb-6">
            Your Feedback Timeline
          </h2>
          
          {filteredFeedbacks.length === 0 ? (
            <div className="text-center py-12">
              <div className="text-gray-400 text-5xl mb-4">üì≠</div>
              <h3 className="text-lg font-semibold text-gray-700 mb-2">
                No Feedback Yet
              </h3>
              <p className="text-gray-500">
                {state.timeFilter === 'all'
                  ? "You haven't received any feedback yet."
                  : `No feedback in the last ${state.timeFilter}.`}
              </p>
            </div>
          ) : (
            <div className="space-y-6">
              {filteredFeedbacks.map((feedback) => (
                <FeedbackCard key={feedback.id} feedback={feedback} />
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default EmployeeDashboard;
