import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import {
  FiArrowLeft,
  FiStar,
  FiThumbsUp,
  FiTrendingUp,
  FiClock,
  FiUser,
  FiAlertCircle,
  FiCheckCircle,
  FiMessageSquare,
} from "react-icons/fi";

const SentimentIndicator = ({ sentiment }) => {
  const colors = {
    positive: "bg-green-100 text-green-800",
    neutral: "bg-blue-100 text-blue-800",
    negative: "bg-red-100 text-red-800",
  };

  return (
    <span
      className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${colors[sentiment]}`}
    >
      {sentiment.charAt(0).toUpperCase() + sentiment.slice(1)}
    </span>
  );
};

const MyFeedback = () => {
  const { employeeID } = useParams();
  const navigate = useNavigate();
  const [acknowledging, setAcknowledging] = useState(null);
  const [ackComment, setAckComment] = useState("");
  const [expandedFeedback, setExpandedFeedback] = useState(null);

  const [state, setState] = useState({
    feedbacks: [],
    loading: true,
    error: null,
    employeeInfo: null,
    isAuthorized: true,
  });

  const { feedbacks, loading, error, employeeInfo, isAuthorized } = state;

  useEffect(() => {
    const fetchEmployeeFeedback = async () => {
      try {
        const parsedEmployeeID = parseInt(employeeID);
        if (isNaN(parsedEmployeeID) || parsedEmployeeID <= 0) {
          throw new Error("Invalid employee ID");
        }

        setState((prev) => ({ ...prev, loading: true, error: null }));
        const userData = JSON.parse(localStorage.getItem("userData"));

        if (!userData?.token) {
          throw new Error("Authentication token missing");
        }

        const [feedbackRes, employeeRes] = await Promise.all([
          axios.get(`http://localhost:8000/feedback/employee/${parsedEmployeeID}`, {
            headers: { Authorization: `Bearer ${userData.token}` },
          }),
          axios.get(`http://localhost:8000/employees/${parsedEmployeeID}`, {
            headers: { Authorization: `Bearer ${userData.token}` },
          }),
        ]);

        setState({
          feedbacks: feedbackRes.data,
          employeeInfo: employeeRes.data,
          loading: false,
          error: null,
          isAuthorized: true,
        });
      } catch (err) {
        if (err.response?.status === 403) {
          setState((prev) => ({
            ...prev,
            loading: false,
            isAuthorized: false,
            error: "You are not authorized to view this feedback",
          }));
        } else {
          setState((prev) => ({
            ...prev,
            loading: false,
            error:
              err.message ||
              err.response?.data?.detail ||
              "Failed to load feedback",
          }));
        }
      }
    };

    fetchEmployeeFeedback();
  }, [employeeID]);

  const handleAcknowledge = async (feedbackId) => {
    try {
      const userData = JSON.parse(localStorage.getItem("userData"));
      if (!userData?.token) throw new Error("Authentication token missing");

      const response = await axios.post(
        `http://localhost:8000/feedback/${feedbackId}/acknowledge`,
        { comment: ackComment },
        { headers: { Authorization: `Bearer ${userData.token}` } }
      );

      setState(prev => ({
        ...prev,
        feedbacks: prev.feedbacks.map(f => 
          f.id === feedbackId 
            ? { 
                ...f, 
                is_acknowledged: true, 
                acknowledgment_comment: ackComment,
                acknowledged_at: new Date().toISOString()
              } 
            : f
        )
      }));

      setAcknowledging(null);
      setAckComment("");
    } catch (err) {
      console.error("Acknowledgment failed:", err);
      setState(prev => ({
        ...prev,
        error: err.response?.data?.detail || "Failed to acknowledge feedback"
      }));
    }
  };

  const toggleExpandFeedback = (feedbackId) => {
    setExpandedFeedback(expandedFeedback === feedbackId ? null : feedbackId);
  };

  const LoadingSpinner = () => (
    <div className="flex items-center justify-center h-64">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
    </div>
  );

  const ErrorDisplay = () => (
    <div className="bg-white rounded-xl shadow-md p-6 text-center">
      <div className="text-red-500 text-5xl mb-4 flex justify-center">
        <FiAlertCircle />
      </div>
      <h2 className="text-xl font-bold text-gray-800 mb-2">
        Error Loading Feedback
      </h2>
      <p className="text-gray-600 mb-4">{error}</p>
      <button
        onClick={() => navigate(-1)}
        className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
      >
        Go Back
      </button>
    </div>
  );

  const UnauthorizedView = () => (
    <div className="bg-white rounded-xl shadow-md p-6 text-center">
      <div className="text-yellow-500 text-5xl mb-4 flex justify-center">
        <FiAlertCircle />
      </div>
      <h2 className="text-xl font-bold text-gray-800 mb-2">Access Denied</h2>
      <p className="text-gray-600 mb-4">
        You don't have permission to view this employee's feedback
      </p>
      <button
        onClick={() => navigate("/")}
        className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
      >
        Back to Dashboard
      </button>
    </div>
  );

  const EmptyState = () => (
    <div className="bg-white rounded-xl shadow-md p-8 text-center">
      <div className="text-gray-400 text-5xl mb-4">ðŸ“­</div>
      <h3 className="text-xl font-semibold text-gray-700 mb-2">
        No Feedback Received Yet
      </h3>
      <p className="text-gray-500 mb-6">
        You haven't received any feedback at this time.
      </p>
      <button
        onClick={() => navigate("/")}
        className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
      >
        Back to Dashboard
      </button>
    </div>
  );

  if (!isAuthorized) {
    return (
      <div className="min-h-screen bg-gray-100 p-6">
        <div className="max-w-5xl mx-auto">
          <UnauthorizedView />
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 p-6">
        <div className="max-w-5xl mx-auto">
          <LoadingSpinner />
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-100 p-6">
        <div className="max-w-5xl mx-auto">
          <ErrorDisplay />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 py-6 px-4 md:px-8">
      <div className="max-w-5xl mx-auto">
        <button
          onClick={() => navigate(-1)}
          className="flex items-center text-indigo-600 hover:text-indigo-800 mb-6"
        >
          <FiArrowLeft className="mr-2" /> Back
        </button>

        {employeeInfo && (
          <div className="bg-white rounded-xl shadow p-6 mb-8">
            <div className="flex items-center gap-4">
              <div className="bg-indigo-100 p-3 rounded-full text-indigo-600">
                <FiUser className="text-2xl" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-800">{employeeInfo.name}</h1>
                <p className="text-gray-500 text-sm">{employeeInfo.email}</p>
              </div>
            </div>
          </div>
        )}

        {feedbacks.length === 0 ? (
          <EmptyState />
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
            {feedbacks.map((feedback) => (
              <div
                key={feedback.id}
                className="bg-white rounded-xl shadow-md p-5 cursor-pointer hover:shadow-lg transition"
                onClick={() => toggleExpandFeedback(feedback.id)}
              >
                <div className="flex justify-between items-center mb-2">
                  <span className="text-gray-600 text-sm flex items-center">
                    <FiClock className="mr-1" />
                    {new Date(feedback.created_at).toLocaleDateString()}
                  </span>
                  <SentimentIndicator sentiment={feedback.sentiment} />
                </div>

                <div className="mb-2">
                  <h4 className="font-semibold text-gray-700 mb-1">Rating</h4>
                  <div className="flex items-center bg-yellow-50 px-2 py-1 rounded w-max">
                    <FiStar className="text-yellow-400 mr-1" />
                    <span className="text-sm font-medium text-yellow-700">{feedback.rating}/5</span>
                  </div>
                </div>

                <div className="mb-2">
                  <h4 className="font-semibold text-green-700 text-sm">Strengths</h4>
                  <p className="text-gray-700 text-sm line-clamp-3">{feedback.strengths}</p>
                </div>

                <div className="mb-2">
                  <h4 className="font-semibold text-blue-700 text-sm">Areas to Improve</h4>
                  <p className="text-gray-700 text-sm line-clamp-3">{feedback.areas_to_improve}</p>
                </div>

                <div className="text-right mt-4">
                  {feedback.is_acknowledged ? (
                    <span className="text-green-600 text-sm flex items-center justify-end">
                      <FiCheckCircle className="mr-1" />
                      Acknowledged
                    </span>
                  ) : (
                    <button
                      className="text-indigo-600 text-sm hover:underline"
                      onClick={(e) => {
                        e.stopPropagation();
                        setAcknowledging(feedback.id);
                      }}
                    >
                      Acknowledge
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Modal for Full Feedback and Acknowledgment */}
        {expandedFeedback && (
          <div className="fixed inset-0 bg-black bg-opacity-40 z-50 flex items-center justify-center">
            <div className="bg-white rounded-xl shadow-lg w-full max-w-2xl p-6 relative">
              <button
                onClick={() => setExpandedFeedback(null)}
                className="absolute top-3 right-3 text-gray-400 hover:text-gray-600 text-xl"
              >
                âœ•
              </button>
              {(() => {
                const feedback = feedbacks.find((f) => f.id === expandedFeedback);
                return feedback ? (
                  <>
                    <h2 className="text-xl font-bold mb-4">Feedback Detail</h2>
                    <p className="text-gray-600 text-sm mb-2">
                      <FiClock className="inline-block mr-1" />
                      {new Date(feedback.created_at).toLocaleString()}
                    </p>
                    <div className="mb-4">
                      <h4 className="font-semibold text-green-700 mb-1">Strengths</h4>
                      <p className="text-gray-800">{feedback.strengths}</p>
                    </div>
                    <div className="mb-4">
                      <h4 className="font-semibold text-blue-700 mb-1">Areas to Improve</h4>
                      <p className="text-gray-800">{feedback.areas_to_improve}</p>
                    </div>
                    {feedback.is_acknowledged ? (
                      <div className="bg-green-50 p-4 rounded-lg border mt-4">
                        <div className="flex items-center text-green-600">
                          <FiCheckCircle className="mr-2" />
                          Acknowledged on {new Date(feedback.acknowledged_at).toLocaleDateString()}
                        </div>
                        <p className="italic mt-2 text-gray-700">
                          "{feedback.acknowledgment_comment || 'No comment'}"
                        </p>
                      </div>
                    ) : acknowledging === feedback.id ? (
                      <div className="mt-4">
                        <textarea
                          className="w-full p-3 border rounded mb-3"
                          rows="3"
                          placeholder="Optional comment..."
                          value={ackComment}
                          onChange={(e) => setAckComment(e.target.value)}
                        />
                        <div className="flex justify-end space-x-2">
                          <button
                            onClick={() => {
                              setAcknowledging(null);
                              setAckComment("");
                            }}
                            className="px-4 py-2 border rounded text-gray-600 hover:bg-gray-100"
                          >
                            Cancel
                          </button>
                          <button
                            onClick={() => handleAcknowledge(feedback.id)}
                            className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700"
                          >
                            Confirm
                          </button>
                        </div>
                      </div>
                    ) : (
                      <div className="mt-4 text-right">
                        <button
                          onClick={() => setAcknowledging(feedback.id)}
                          className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
                        >
                          Acknowledge
                        </button>
                      </div>
                    )}
                  </>
                ) : null;
              })()}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MyFeedback;
