import React, { useState } from 'react';
import axios from 'axios';
import { 
  FiCheckCircle, 
  FiXCircle, 
  FiStar, 
  FiLoader,
  FiUser,
  FiThumbsUp,
  FiTrendingUp,
  FiSmile,
  FiMeh,
  FiFrown
} from 'react-icons/fi';

function FeedbackForm() {
  const [formData, setFormData] = useState({
    employee_id: '',
    strengths: '',
    areas_to_improve: '',
    sentiment: 'positive',
    rating: 0
  });

  const [showModal, setShowModal] = useState(false);
  const [modalContent, setModalContent] = useState({
    success: false,
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRating = (value) => {
    setFormData({ ...formData, rating: value });
  };

  const isFormValid = () => {
    return (
      formData.employee_id.trim() !== '' &&
      formData.strengths.trim() !== '' &&
      formData.areas_to_improve.trim() !== '' &&
      formData.rating > 0
    );
  };

const handleSubmit = async (e) => {
  e.preventDefault();
  if (!isFormValid()) return;

  setIsSubmitting(true);
  try {
    const userData = JSON.parse(localStorage.getItem('userData'));
    if (!userData || !userData.token) {
      throw new Error('No authentication token found');
    }
    
    await axios.post('http://localhost:8000/feedback', formData, {
      headers: {
        'Authorization': `Bearer ${userData.token}`
      }
    });
    
    setModalContent({
      success: true,
      message: 'Feedback submitted successfully!'
    });
    setShowModal(true);
    resetForm();
  } catch (err) {
    setModalContent({
      success: false,
      message: err.response?.data?.detail || err.message || 'Error submitting feedback. Please try again.'
    });
    setShowModal(true);
  } finally {
    setIsSubmitting(false);
  }
};

  const resetForm = () => {
    setFormData({
      employee_id: '',
      strengths: '',
      areas_to_improve: '',
      sentiment: 'positive',
      rating: 0
    });
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-sm w-full mx-4 shadow-xl text-center animate-popIn">
            {modalContent.success ? (
              <FiCheckCircle className="text-green-500 text-5xl mx-auto mb-4" />
            ) : (
              <FiXCircle className="text-red-500 text-5xl mx-auto mb-4" />
            )}
            <h3 className="text-xl font-semibold mb-2">
              {modalContent.success ? 'Success!' : 'Error!'}
            </h3>
            <p className="text-gray-600 mb-6">{modalContent.message}</p>
            <button
              onClick={closeModal}
              className={`px-6 py-2 rounded-lg font-medium ${
                modalContent.success
                  ? 'bg-green-500 hover:bg-green-600 text-white'
                  : 'bg-red-500 hover:bg-red-600 text-white'
              } transition-colors`}
            >
              Close
            </button>
          </div>
        </div>
      )}

      <div className="w-full max-w-md bg-white rounded-xl shadow-md overflow-hidden">
        <div className="bg-gradient-to-r from-indigo-500 to-purple-600 p-6 text-white">
          <h2 className="text-2xl font-bold flex items-center">
            <FiTrendingUp className="mr-2" />
            Employee Feedback
          </h2>
          <p className="text-indigo-100">Share your valuable feedback</p>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-5">
          {/* Employee ID */}
          <div className="space-y-1">
            <label className="flex items-center text-sm font-medium text-gray-700">
              <FiUser className="mr-2" />
              Employee ID
            </label>
            <input
              type="text"
              name="employee_id"
              value={formData.employee_id}
              onChange={handleChange}
              className="w-full border border-gray-200 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:border-transparent"
              placeholder="Enter Employee ID"
              required
            />
          </div>

          {/* Strengths */}
          <div className="space-y-1">
            <label className="flex items-center text-sm font-medium text-gray-700">
              <FiThumbsUp className="mr-2" />
              Strengths
            </label>
            <textarea
              name="strengths"
              value={formData.strengths}
              onChange={handleChange}
              rows="3"
              className="w-full border border-gray-200 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:border-transparent"
              placeholder="What went well..."
              required
            />
          </div>

          {/* Areas to Improve */}
          <div className="space-y-1">
            <label className="flex items-center text-sm font-medium text-gray-700">
              <FiTrendingUp className="mr-2" />
              Areas to Improve
            </label>
            <textarea
              name="areas_to_improve"
              value={formData.areas_to_improve}
              onChange={handleChange}
              rows="3"
              className="w-full border border-gray-200 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:border-transparent"
              placeholder="What needs improvement..."
              required
            />
          </div>

          {/* Sentiment */}
          <div className="space-y-1">
            <label className="flex items-center text-sm font-medium text-gray-700">
              <FiSmile className="mr-2" />
              Overall Sentiment
            </label>
            <div className="flex space-x-4">
              {[
                { value: 'positive', icon: <FiSmile className="mr-1" />, label: 'Positive' },
                { value: 'neutral', icon: <FiMeh className="mr-1" />, label: 'Neutral' },
                { value: 'negative', icon: <FiFrown className="mr-1" />, label: 'Negative' }
              ].map((option) => (
                <label key={option.value} className="flex items-center">
                  <input
                    type="radio"
                    name="sentiment"
                    value={option.value}
                    checked={formData.sentiment === option.value}
                    onChange={handleChange}
                    className="mr-2 text-indigo-600 focus:ring-indigo-500"
                  />
                  <span className="flex items-center">
                    {option.icon}
                    {option.label}
                  </span>
                </label>
              ))}
            </div>
          </div>

          {/* Star Rating */}
          <div className="space-y-1">
            <label className="flex items-center text-sm font-medium text-gray-700">
              <FiStar className="mr-2" />
              Performance Rating
            </label>
            <div className="flex space-x-1">
              {[1, 2, 3, 4, 5].map((value) => (
                <button
                  type="button"
                  key={value}
                  onClick={() => handleRating(value)}
                  className={`text-2xl transition-colors ${
                    formData.rating >= value ? 'text-yellow-400' : 'text-gray-300'
                  } hover:text-yellow-500`}
                >
                  <FiStar />
                </button>
              ))}
            </div>
            <p className="text-xs text-gray-500 mt-1">
              Selected: {formData.rating} star{formData.rating !== 1 ? 's' : ''}
            </p>
          </div>

          {/* Submit Button */}
          <div className="pt-4">
            <button
              type="submit"
              disabled={!isFormValid() || isSubmitting}
              className={`w-full flex items-center justify-center py-2 px-4 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white ${
                !isFormValid() || isSubmitting
                  ? 'bg-gray-400 cursor-not-allowed'
                  : 'bg-indigo-600 hover:bg-indigo-700'
              } focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors`}
            >
              {isSubmitting ? (
                <>
                  <FiLoader className="animate-spin mr-2" />
                  Processing...
                </>
              ) : (
                <>
                  <FiCheckCircle className="mr-2" />
                  Submit Feedback
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default FeedbackForm;
