// hooks/useAuth.js
import { useState, useEffect, useCallback } from "react";

let logoutTimer;

export const useAuth = () => {
  const [authState, setAuthState] = useState({
    isLoggedIn: false,
    userId: null,        // email or username (from token 'sub')
    employeeId: null,    // actual user ID from database
    token: null,
    role: null,
    name: null,
    tokenExpiration: null
  });

  const login = useCallback(async (email, password) => {
    try {
      const response = await fetch("http://localhost:8000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || "Login failed");
      }

      const { token } = await response.json();
      const decoded = JSON.parse(atob(token.split('.')[1]));
      
      // Validate token structure
      if (!decoded.sub || !decoded.userId || !decoded.role) {
        throw new Error("Invalid token structure");
      }

      const expirationDate = new Date(decoded.exp * 1000);
      
      const newAuthState = {
        isLoggedIn: true,
        userId: decoded.sub,
        employeeId: decoded.userId,
        token,
        role: decoded.role,
        name: decoded.name || "User",
        tokenExpiration: expirationDate
      };

      setAuthState(newAuthState);
      
      localStorage.setItem(
        "userData",
        JSON.stringify({
          userId: decoded.sub,
          employeeId: decoded.userId,
          token,
          role: decoded.role,
          name: decoded.name,
          expiration: expirationDate.toISOString(),
        })
      );
      
      return true;
    } catch (err) {
      console.error("Login error:", err.message);
      return false;
    }
  }, []);

  const register = useCallback(async (name, email, password, role = "employee") => {
    try {
      const response = await fetch("http://localhost:8000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, password, role }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || "Registration failed");
      }
      
      return { success: true };
    } catch (err) {
      console.error("Registration error:", err.message);
      return { success: false, error: err.message };
    }
  }, []);

  const logout = useCallback(() => {
    setAuthState({
      isLoggedIn: false,
      userId: null,
      employeeId: null,
      token: null,
      role: null,
      name: null,
      tokenExpiration: null
    });
    localStorage.removeItem("userData");
    if (logoutTimer) clearTimeout(logoutTimer);
  }, []);

  // Auto-logout timer
  useEffect(() => {
    if (authState.token && authState.tokenExpiration) {
      const remainingTime = authState.tokenExpiration.getTime() - Date.now();
      if (remainingTime > 0) {
        logoutTimer = setTimeout(logout, remainingTime);
      } else {
        logout();
      }
    }
    return () => clearTimeout(logoutTimer);
  }, [authState.token, authState.tokenExpiration, logout]);

  // Auto-login from localStorage
  useEffect(() => {
    const initializeAuth = async () => {
      const storedData = JSON.parse(localStorage.getItem("userData"));
      
      if (storedData?.token && new Date(storedData.expiration) > new Date()) {
        try {
          // Optional: Verify token with backend
          const response = await fetch("http://localhost:8000/verify-token", {
            headers: { Authorization: `Bearer ${storedData.token}` }
          });
          
          if (response.ok) {
            setAuthState({
              isLoggedIn: true,
              userId: storedData.userId,
              employeeId: storedData.employeeId,
              token: storedData.token,
              role: storedData.role,
              name: storedData.name,
              tokenExpiration: new Date(storedData.expiration)
            });
          } else {
            localStorage.removeItem("userData");
          }
        } catch {
          localStorage.removeItem("userData");
        }
      }
    };

    initializeAuth();
  }, []);

  return {
    ...authState,
    login,
    logout,
    register
  };
};
