// hooks/useAuth.js
import { useState, useEffect, useCallback } from "react";

let logoutTimer;

export const useAuth = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId, setUserId] = useState(null);
  const [token, setToken] = useState(null);
  const [role, setRole] = useState(null);
  const [name, setName] = useState(null);
  const [tokenExpirationDate, setTokenExpirationDate] = useState(null);
  const [authState, setAuthState] = useState(false);

  const login = useCallback(async (email, password) => {
    try {
      const response = await fetch("http://localhost:8000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email,
          password,
        }),
      });

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.detail || "Login failed");
      }

      const { token } = data;
      
      // Decode token to get user info (you might want to verify this on backend)
      const decoded = JSON.parse(atob(token.split('.')[1]));
      
      const expirationDate = new Date(decoded.exp * 1000);
      
      setIsLoggedIn(true);
      setToken(token);
      setUserId(decoded.sub); // email from token
      setRole(decoded.role || "employee"); // Add role to your JWT payload
      setName(decoded.name || "User"); // Add name to your JWT payload
      setAuthState(true);
      setTokenExpirationDate(expirationDate);
      
      localStorage.setItem(
        "userData",
        JSON.stringify({
          userId: decoded.sub,
          token,
          role: decoded.role,
          name: decoded.name,
          expiration: expirationDate.toISOString(),
        })
      );
      
      return true;
    } catch (err) {
      console.error("Login error:", err);
      return false;
    }
  }, []);

  const register = useCallback(async (name, email, password, role) => {
    try {
      const response = await fetch("http://localhost:8000/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name,
          email,
          password,
          role,
        }),
      });

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.detail || "Registration failed");
      }
      
      return true;
    } catch (err) {
      console.error("Registration error:", err);
      return false;
    }
  }, []);

  const logout = useCallback(() => {
    setIsLoggedIn(false);
    setToken(null);
    setUserId(null);
    setRole(null);
    setName(null);
    setTokenExpirationDate(null);
    setAuthState({ isLoggedIn: false });
    localStorage.removeItem("userData");
  }, []);

  useEffect(() => {
    if (token && tokenExpirationDate) {
      const remainingTime = tokenExpirationDate.getTime() - new Date().getTime();
      logoutTimer = setTimeout(logout, remainingTime);
    } else {
      clearTimeout(logoutTimer);
    }
  }, [token, logout, tokenExpirationDate]);

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem("userData"));
    if (
      storedData &&
      storedData.token &&
      new Date(storedData.expiration) > new Date()
    ) {
      setIsLoggedIn(true);
      setToken(storedData.token);
      setUserId(storedData.userId);
      setRole(storedData.role);
      setName(storedData.name);
      setTokenExpirationDate(new Date(storedData.expiration));
    }
  }, []);

  return {
    ...authState,
    isLoggedIn,
    userId,
    token,
    role,
    name,
    login,
    logout,
    register,
  };
};
