export default function FeedbackCard({ feedback, onAcknowledge, isEmployeeView }) {
  const sentimentColors = {
    positive: 'bg-green-100 border-green-400',
    neutral: 'bg-yellow-100 border-yellow-400',
    negative: 'bg-red-100 border-red-400'
  };

  return (
    <div className={`border-l-4 p-4 mb-4 rounded ${sentimentColors[feedback.sentiment]}`}>
      <div className="flex justify-between items-start">
        <div>
          <h4 className="font-medium text-lg">{feedback.title}</h4>
          <p className="text-sm text-gray-500">
            {new Date(feedback.created_at).toLocaleDateString()}
          </p>
        </div>
        {isEmployeeView && !feedback.acknowledged && (
          <button
            onClick={() => onAcknowledge(feedback.id)}
            className="bg-blue-600 text-white px-3 py-1 rounded text-sm"
          >
            Acknowledge
          </button>
        )}
      </div>

      <div className="mt-3">
        <div className="mb-3">
          <h5 className="font-medium text-gray-700">Strengths</h5>
          <p className="text-gray-600">{feedback.strengths}</p>
        </div>
        <div>
          <h5 className="font-medium text-gray-700">Areas to Improve</h5>
          <p className="text-gray-600">{feedback.areasToImprove}</p>
        </div>
      </div>

      {feedback.acknowledged && (
        <div className="mt-2 text-sm text-gray-500">
          Acknowledged on {new Date(feedback.acknowledged_at).toLocaleDateString()}
        </div>
      )}
    </div>
  );
}
