import React, { useState } from 'react';
import axios from 'axios';
import { FaCheckCircle, FaTimesCircle, FaStar, FaSpinner } from 'react-icons/fa';

function FeedbackForm() {
  const [formData, setFormData] = useState({
    employee_id: '',
    strengths: '',
    areas_to_improve: '',
    sentiment: 'positive',
    rating: 0
  });

  const [showModal, setShowModal] = useState(false);
  const [modalContent, setModalContent] = useState({
    success: false,
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRating = (value) => {
    setFormData({ ...formData, rating: value });
  };

  const isFormValid = () => {
    return (
      formData.employee_id.trim() !== '' &&
      formData.strengths.trim() !== '' &&
      formData.areas_to_improve.trim() !== '' &&
      formData.rating > 0
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isFormValid()) return;

    setIsSubmitting(true);
    try {
      await axios.post('http://localhost:5000/feedback', formData);
      setModalContent({
        success: true,
        message: 'Feedback submitted successfully!'
      });
      setShowModal(true);
      resetForm();
    } catch (err) {
      setModalContent({
        success: false,
        message: 'Error submitting feedback. Please try again.'
      });
      setShowModal(true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetForm = () => {
    setFormData({
      employee_id: '',
      strengths: '',
      areas_to_improve: '',
      sentiment: 'positive',
      rating: 0
    });
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="animate-fadeIn w-full max-w-2xl bg-white rounded-3xl shadow-2xl p-8 mx-auto border border-gray-100 relative">
      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl p-6 max-w-sm w-full mx-4 shadow-xl text-center">
            {modalContent.success ? (
              <FaCheckCircle className="text-green-500 text-5xl mx-auto mb-4" />
            ) : (
              <FaTimesCircle className="text-red-500 text-5xl mx-auto mb-4" />
            )}
            <h3 className="text-xl font-semibold mb-2">
              {modalContent.success ? 'Success!' : 'Error!'}
            </h3>
            <p className="text-gray-600 mb-6">{modalContent.message}</p>
            <button
              onClick={closeModal}
              className={`px-6 py-2 rounded-xl font-medium ${
                modalContent.success
                  ? 'bg-green-500 hover:bg-green-600 text-white'
                  : 'bg-red-500 hover:bg-red-600 text-white'
              }`}
            >
              Close
            </button>
          </div>
        </div>
      )}

      <h2 className="text-3xl font-bold text-blue-800 mb-6 text-center">Submit Feedback</h2>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Employee ID */}
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Employee ID</label>
          <input
            type="text"
            name="employee_id"
            value={formData.employee_id}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-xl px-4 py-2 text-gray-800 focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            placeholder="Enter Employee ID"
            required
          />
        </div>

        {/* Strengths */}
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Strengths</label>
          <textarea
            name="strengths"
            value={formData.strengths}
            onChange={handleChange}
            rows="3"
            className="w-full border border-gray-300 rounded-xl px-4 py-2 text-gray-800 resize-none focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            placeholder="What went well..."
            required
          />
        </div>

        {/* Areas to Improve */}
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Areas to Improve</label>
          <textarea
            name="areas_to_improve"
            value={formData.areas_to_improve}
            onChange={handleChange}
            rows="3"
            className="w-full border border-gray-300 rounded-xl px-4 py-2 text-gray-800 resize-none focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            placeholder="What needs improvement..."
            required
          />
        </div>

        {/* Sentiment */}
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Overall Sentiment</label>
          <select
            name="sentiment"
            value={formData.sentiment}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-xl px-4 py-2 text-gray-800 bg-white shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
          >
            <option value="positive">Positive</option>
            <option value="neutral">Neutral</option>
            <option value="negative">Negative</option>
          </select>
        </div>

        {/* Star Rating */}
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Performance Rating</label>
          <div className="flex space-x-2 text-2xl">
            {[1, 2, 3, 4, 5].map((value) => (
              <button
                type="button"
                key={value}
                onClick={() => handleRating(value)}
                className={`cursor-pointer transform hover:scale-110 transition ${
                  formData.rating >= value ? 'text-yellow-400' : 'text-gray-300'
                }`}
              >
                <FaStar />
              </button>
            ))}
          </div>
          <p className="text-sm text-gray-500 mt-1">Rating: {formData.rating} / 5</p>
        </div>

        {/* Submit */}
        <div className="text-center pt-4">
          <button
            type="submit"
            disabled={!isFormValid() || isSubmitting}
            className={`flex items-center justify-center space-x-2 font-semibold px-6 py-3 rounded-xl w-full transition duration-300 shadow-md ${
              !isFormValid() || isSubmitting
                ? 'bg-gray-300 text-gray-600 cursor-not-allowed'
                : 'bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white'
            }`}
          >
            {isSubmitting ? (
              <>
                <FaSpinner className="animate-spin" />
                <span>Submitting...</span>
              </>
            ) : (
              <>
                <FaCheckCircle />
                <span>Submit Feedback</span>
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );
}

export default FeedbackForm;
