import React, { useState } from 'react';
import axios from 'axios';
import {
  FiCheckCircle,
  FiXCircle,
  FiStar,
  FiLoader,
  FiUser,
  FiThumbsUp,
  FiTrendingUp,
  FiSmile,
  FiMeh,
  FiFrown,
  FiChevronLeft
} from 'react-icons/fi';

function FeedbackForm() {
  const [formData, setFormData] = useState({
    employee_id: '',
    strengths: '',
    areas_to_improve: '',
    sentiment: 'positive',
    rating: 0
  });

  const [showModal, setShowModal] = useState(false);
  const [modalContent, setModalContent] = useState({
    success: false,
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRating = (value) => {
    setFormData({ ...formData, rating: value });
  };

  const isFormValid = () => {
    return (
      formData.employee_id.trim() !== '' &&
      formData.strengths.trim() !== '' &&
      formData.areas_to_improve.trim() !== '' &&
      formData.rating > 0
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isFormValid()) return;

    setIsSubmitting(true);
    try {
      await axios.post('http://localhost:5000/feedback', formData);
      setModalContent({
        success: true,
        message: 'Feedback submitted successfully!'
      });
      setShowModal(true);
      resetForm();
    } catch (err) {
      setModalContent({
        success: false,
        message: 'Error submitting feedback. Please try again.'
      });
      setShowModal(true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetForm = () => {
    setFormData({
      employee_id: '',
      strengths: '',
      areas_to_improve: '',
      sentiment: 'positive',
      rating: 0
    });
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <>
      {/* Mobile View */}
      <div className="md:hidden min-h-screen bg-gradient-to-b from-indigo-50 to-purple-100">
        {/* Modal */}
        {showModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-xl p-6 w-full max-w-xs shadow-2xl text-center animate-fadeIn">
              {modalContent.success ? (
                <span className="text-5xl">ðŸŽ‰</span>
              ) : (
                <span className="text-5xl">ðŸ˜ž</span>
              )}
              <h3 className="text-xl font-semibold mt-4 mb-2">
                {modalContent.success ? 'Success!' : 'Error!'}
              </h3>
              <p className="text-gray-600 mb-6">{modalContent.message}</p>
              <button
                onClick={closeModal}
                className={`w-full py-2 rounded-lg font-medium ${
                  modalContent.success
                    ? 'bg-green-500 hover:bg-green-600'
                    : 'bg-red-500 hover:bg-red-600'
                } text-white transition-colors`}
              >
                Close
              </button>
            </div>
          </div>
        )}

        <div className="p-4">
          <button className="flex items-center text-indigo-600 mb-4">
            <FiChevronLeft className="mr-1" />
            Back
          </button>
          
          <div className="bg-white/90 backdrop-blur-sm rounded-xl shadow-lg overflow-hidden border border-white/30">
            <div className="bg-gradient-to-r from-indigo-500 to-purple-600 p-5 text-white">
              <h2 className="text-2xl font-bold flex items-center">
                <FiTrendingUp className="mr-2" />
                Feedback
              </h2>
            </div>

            <form onSubmit={handleSubmit} className="p-5 space-y-4">
              {/* Form fields remain the same as desktop but with mobile spacing */}
              <div className="space-y-2">
                <label className="flex items-center text-sm font-medium text-gray-700">
                  <FiUser className="mr-2" />
                  Employee ID
                </label>
                <input
                  type="text"
                  name="employee_id"
                  value={formData.employee_id}
                  onChange={handleChange}
                  className="w-full border border-gray-200 rounded-lg px-4 py-2 text-sm"
                  placeholder="Enter Employee ID"
                  required
                />
              </div>

              {/* Other fields... */}

              <button
                type="submit"
                disabled={!isFormValid() || isSubmitting}
                className={`w-full py-3 rounded-lg font-medium text-white ${
                  !isFormValid() || isSubmitting
                    ? 'bg-gray-400'
                    : 'bg-indigo-600 hover:bg-indigo-700'
                } transition-colors`}
              >
                {isSubmitting ? (
                  <span className="flex items-center justify-center">
                    <FiLoader className="animate-spin mr-2" />
                    Submitting...
                  </span>
                ) : (
                  <span className="flex items-center justify-center">
                    <FiCheckCircle className="mr-2" />
                    Submit
                  </span>
                )}
              </button>
            </form>
          </div>
        </div>
      </div>

      {/* Desktop View */}
      <div className="hidden md:flex min-h-screen bg-gradient-to-tr from-purple-100 to-indigo-200 items-center justify-center p-8">
        <div className="animate-fadeIn w-full max-w-2xl bg-white/30 backdrop-blur-md border border-white/20 rounded-xl shadow-2xl overflow-hidden transition-all duration-300">
          <div className="bg-gradient-to-br from-indigo-500 to-purple-500 p-8 text-white shadow-md">
            <h2 className="text-3xl font-bold flex items-center">
              <FiTrendingUp className="mr-3" />
              Employee Performance Feedback
            </h2>
            <p className="text-sm opacity-90 mt-1">Share your valuable insights to help improve team performance</p>
          </div>

          <form onSubmit={handleSubmit} className="p-8 grid grid-cols-2 gap-6">
            <div className="col-span-2 space-y-1">
              <label className="flex items-center text-sm font-medium text-gray-700">
                <FiUser className="mr-2" />
                Employee ID
              </label>
              <input
                type="text"
                name="employee_id"
                value={formData.employee_id}
                onChange={handleChange}
                className="w-full border border-gray-200 rounded-lg px-4 py-2 focus:ring-2 focus:ring-indigo-400"
                placeholder="Enter Employee ID"
                required
              />
            </div>

            <div className="col-span-1 space-y-1">
              <label className="flex items-center text-sm font-medium text-gray-700">
                <FiThumbsUp className="mr-2" />
                Strengths
              </label>
              <textarea
                name="strengths"
                value={formData.strengths}
                onChange={handleChange}
                rows="4"
                className="w-full border border-gray-200 rounded-lg px-4 py-2 focus:ring-2 focus:ring-indigo-400"
                placeholder="What went well..."
                required
              />
            </div>

            <div className="col-span-1 space-y-1">
              <label className="flex items-center text-sm font-medium text-gray-700">
                <FiTrendingUp className="mr-2" />
                Areas to Improve
              </label>
              <textarea
                name="areas_to_improve"
                value={formData.areas_to_improve}
                onChange={handleChange}
                rows="4"
                className="w-full border border-gray-200 rounded-lg px-4 py-2 focus:ring-2 focus:ring-indigo-400"
                placeholder="What needs improvement..."
                required
              />
            </div>

            <div className="col-span-1 space-y-1">
              <label className="flex items-center text-sm font-medium text-gray-700">
                <FiSmile className="mr-2" />
                Overall Sentiment
              </label>
              <div className="flex space-x-4">
                {[{ value: 'positive', icon: <FiSmile /> }, /* ... */].map(option => (
                  <label key={option.value} className="flex items-center space-x-2">
                    <input
                      type="radio"
                      name="sentiment"
                      value={option.value}
                      checked={formData.sentiment === option.value}
                      onChange={handleChange}
                      className="text-indigo-600 focus:ring-indigo-500"
                    />
                    <span className="flex items-center">
                      {option.icon}
                    </span>
                  </label>
                ))}
              </div>
            </div>

            <div className="col-span-1 space-y-1">
              <label className="flex items-center text-sm font-medium text-gray-700">
                <FiStar className="mr-2" />
                Performance Rating
              </label>
              <div className="flex space-x-2">
                {[1, 2, 3, 4, 5].map(value => (
                  <button
                    type="button"
                    key={value}
                    onClick={() => handleRating(value)}
                    className={`text-2xl ${formData.rating >= value ? 'text-yellow-400' : 'text-gray-300'} hover:text-yellow-500`}
                  >
                    <FiStar />
                  </button>
                ))}
              </div>
              <p className="text-xs text-gray-500 mt-1">
                Selected: {formData.rating} star{formData.rating !== 1 ? 's' : ''}
              </p>
            </div>

            <div className="col-span-2 pt-4">
              <button
                type="submit"
                disabled={!isFormValid() || isSubmitting}
                className={`w-full py-3 rounded-lg font-semibold text-white ${
                  !isFormValid() || isSubmitting
                    ? 'bg-gray-400 cursor-not-allowed'
                    : 'bg-gradient-to-r from-indigo-500 to-purple-500 hover:from-indigo-600 hover:to-purple-600'
                } transition-all shadow-md`}
              >
                {isSubmitting ? (
                  <span className="flex items-center justify-center">
                    <FiLoader className="animate-spin mr-2" />
                    Processing Feedback...
                  </span>
                ) : (
                  <span className="flex items-center justify-center">
                    <FiCheckCircle className="mr-2" />
                    Submit Performance Feedback
                  </span>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </>
  );
}

export default FeedbackForm;
