import { useState } from 'react';

export default function FeedbackForm({ employee, onSubmit }) {
  const [formData, setFormData] = useState({
    title: '',
    strengths: '',
    areasToImprove: '',
    sentiment: 'positive'
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      employeeId: employee.id,
      ...formData
    });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-lg font-medium mb-4">Give Feedback to {employee.name}</h3>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-1">Title</label>
          <input
            type="text"
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
            value={formData.title}
            onChange={(e) => setFormData({...formData, title: e.target.value})}
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-1">Strengths</label>
          <textarea
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
            rows={3}
            value={formData.strengths}
            onChange={(e) => setFormData({...formData, strengths: e.target.value})}
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-1">Areas to Improve</label>
          <textarea
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
            rows={3}
            value={formData.areasToImprove}
            onChange={(e) => setFormData({...formData, areasToImprove: e.target.value})}
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-1">Sentiment</label>
          <select
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
            value={formData.sentiment}
            onChange={(e) => setFormData({...formData, sentiment: e.target.value})}
          >
            <option value="positive">Positive</option>
            <option value="neutral">Neutral</option>
            <option value="negative">Negative</option>
          </select>
        </div>

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition"
        >
          Submit Feedback
        </button>
      </form>
    </div>
  );
}
