import React, { useState } from 'react';
import axios from 'axios';

function FeedbackForm() {
  const [formData, setFormData] = useState({
    employee_id: '',
    strengths: '',
    areas_to_improve: '',
    sentiment: 'positive',
    rating: 0
  });

  const [showModal, setShowModal] = useState(false);

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleRating = (value) => {
    setFormData({...formData, rating: value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/feedback', formData);
      setShowModal(true); // show success modal
      setFormData({
        employee_id: '',
        strengths: '',
        areas_to_improve: '',
        sentiment: 'positive',
        rating: 0
      });
    } catch (err) {
      alert('‚ùå Error submitting feedback');
    }
  };

  return (
    <div className="w-full max-w-2xl bg-white rounded-2xl shadow-lg p-6 mx-auto mt-8 relative">
      <h2 className="text-2xl font-semibold text-blue-800 mb-6 text-center">
        Submit Feedback
      </h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        {/* ... All previous form fields (Employee ID, Strengths, Areas, Sentiment, Star Rating) ... */}

        <div className="text-center pt-4">
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-2 rounded-xl w-full sm:w-auto"
          >
            Submit Feedback
          </button>
        </div>
      </form>

      {/* ‚úÖ Success Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-6 shadow-xl w-11/12 max-w-sm text-center">
            <h3 className="text-xl font-semibold text-green-700 mb-4">üéâ Feedback Submitted!</h3>
            <p className="text-gray-600 mb-6">Your feedback has been recorded successfully.</p>
            <button
              onClick={() => setShowModal(false)}
              className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-xl"
            >
              OK
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default FeedbackForm;
