import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import {
  FiArrowLeft,
  FiStar,
  FiThumbsUp,
  FiTrendingUp,
  FiClock,
  FiUser,
  FiAlertCircle,
  FiCheckCircle,
  FiMessageSquare,
  FiChevronDown,
  FiChevronUp,
} from "react-icons/fi";

const SentimentIndicator = ({ sentiment }) => {
  const colors = {
    positive: "bg-green-100 text-green-800",
    neutral: "bg-blue-100 text-blue-800",
    negative: "bg-red-100 text-red-800",
  };

  return (
    <span
      className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${colors[sentiment]}`}
    >
      {sentiment.charAt(0).toUpperCase() + sentiment.slice(1)}
    </span>
  );
};

const MyFeedback = () => {
  const { employeeID } = useParams();
  const navigate = useNavigate();
  const [acknowledging, setAcknowledging] = useState(null);
  const [ackComment, setAckComment] = useState("");
  const [expandedFeedback, setExpandedFeedback] = useState(null);

  const [state, setState] = useState({
    feedbacks: [],
    loading: true,
    error: null,
    employeeInfo: null,
    isAuthorized: true,
  });

  const { feedbacks, loading, error, employeeInfo, isAuthorized } = state;

  useEffect(() => {
    const fetchEmployeeFeedback = async () => {
      try {
        const parsedEmployeeID = parseInt(employeeID);
        if (isNaN(parsedEmployeeID) || parsedEmployeeID <= 0) {
          throw new Error("Invalid employee ID");
        }

        setState((prev) => ({ ...prev, loading: true, error: null }));
        const userData = JSON.parse(localStorage.getItem("userData"));

        if (!userData?.token) {
          throw new Error("Authentication token missing");
        }

        const [feedbackRes, employeeRes] = await Promise.all([
          axios.get(`http://localhost:8000/feedback/employee/${parsedEmployeeID}`, {
            headers: { Authorization: `Bearer ${userData.token}` },
          }),
          axios.get(`http://localhost:8000/employees/${parsedEmployeeID}`, {
            headers: { Authorization: `Bearer ${userData.token}` },
          }),
        ]);

        setState({
          feedbacks: feedbackRes.data,
          employeeInfo: employeeRes.data,
          loading: false,
          error: null,
          isAuthorized: true,
        });
      } catch (err) {
        if (err.response?.status === 403) {
          setState((prev) => ({
            ...prev,
            loading: false,
            isAuthorized: false,
            error: "You are not authorized to view this feedback",
          }));
        } else {
          setState((prev) => ({
            ...prev,
            loading: false,
            error:
              err.message ||
              err.response?.data?.detail ||
              "Failed to load feedback",
          }));
        }
      }
    };

    fetchEmployeeFeedback();
  }, [employeeID]);

  const handleAcknowledge = async (feedbackId) => {
    try {
      const userData = JSON.parse(localStorage.getItem("userData"));
      if (!userData?.token) throw new Error("Authentication token missing");

      const response = await axios.post(
        `http://localhost:8000/feedback/${feedbackId}/acknowledge`,
        { comment: ackComment },
        { headers: { Authorization: `Bearer ${userData.token}` } }
      );

      setState(prev => ({
        ...prev,
        feedbacks: prev.feedbacks.map(f => 
          f.id === feedbackId 
            ? { 
                ...f, 
                is_acknowledged: true, 
                acknowledgment_comment: ackComment,
                acknowledged_at: new Date().toISOString()
              } 
            : f
        )
      }));

      setAcknowledging(null);
      setAckComment("");
    } catch (err) {
      console.error("Acknowledgment failed:", err);
      setState(prev => ({
        ...prev,
        error: err.response?.data?.detail || "Failed to acknowledge feedback"
      }));
    }
  };

  const toggleExpandFeedback = (feedbackId) => {
    setExpandedFeedback(expandedFeedback === feedbackId ? null : feedbackId);
  };

  const LoadingSpinner = () => (
    <div className="flex items-center justify-center h-64">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
    </div>
  );

  const ErrorDisplay = () => (
    <div className="bg-white rounded-xl shadow-md p-6 text-center">
      <div className="text-red-500 text-5xl mb-4 flex justify-center">
        <FiAlertCircle />
      </div>
      <h2 className="text-xl font-bold text-gray-800 mb-2">
        Error Loading Feedback
      </h2>
      <p className="text-gray-600 mb-4">{error}</p>
      <button
        onClick={() => navigate(-1)}
        className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
      >
        Go Back
      </button>
    </div>
  );

  const UnauthorizedView = () => (
    <div className="bg-white rounded-xl shadow-md p-6 text-center">
      <div className="text-yellow-500 text-5xl mb-4 flex justify-center">
        <FiAlertCircle />
      </div>
      <h2 className="text-xl font-bold text-gray-800 mb-2">Access Denied</h2>
      <p className="text-gray-600 mb-4">
        You don't have permission to view this employee's feedback
      </p>
      <button
        onClick={() => navigate("/")}
        className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
      >
        Back to Dashboard
      </button>
    </div>
  );

  const EmployeeHeader = () => (
    <div className="bg-white rounded-xl shadow-md p-6 mb-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center">
          <div className="bg-indigo-100 text-indigo-600 rounded-full p-3 mr-4">
            <FiUser className="text-xl" />
          </div>
          <div>
            <h1 className="text-2xl font-bold text-gray-800">
              {employeeInfo.name || "Employee"}
            </h1>
            {employeeInfo.email && (
              <p className="text-gray-500">{employeeInfo.email}</p>
            )}
          </div>
        </div>
        <div className="bg-gray-100 px-3 py-1 rounded-full">
          <span className="text-sm font-medium text-gray-700">
            {feedbacks.length} {feedbacks.length === 1 ? "feedback" : "feedbacks"}
          </span>
        </div>
      </div>
    </div>
  );

  const FeedbackItem = ({ feedback }) => {
    const isExpanded = expandedFeedback === feedback.id;
    const isAcknowledged = feedback.is_acknowledged;
    const isAcknowledging = acknowledging === feedback.id;

    return (
      <div className={`bg-white rounded-xl shadow-md overflow-hidden mb-6 transition-all ${isExpanded ? "ring-2 ring-indigo-200" : ""}`}>
        <div className="p-6">
          <div className="flex justify-between items-start mb-4">
            <div>
              <h3 className="font-medium text-gray-800 flex items-center">
                <FiClock className="mr-2" />
                {new Date(feedback.created_at).toLocaleDateString("en-US", {
                  year: "numeric",
                  month: "short",
                  day: "numeric",
                })}
              </h3>
              {feedback.manager_name && (
                <p className="text-sm text-gray-500 mt-1">
                  From: {feedback.manager_name}
                </p>
              )}
            </div>
            <div className="flex items-center space-x-2">
              <SentimentIndicator sentiment={feedback.sentiment} />
              <div className="flex items-center bg-yellow-50 px-2 py-1 rounded">
                <FiStar className="text-yellow-400 mr-1" />
                <span className="font-medium text-yellow-700">{feedback.rating}/5</span>
              </div>
            </div>
          </div>

          <div className={`grid grid-cols-1 gap-4 mb-4 transition-all ${isExpanded ? "md:grid-cols-2" : ""}`}>
            <div className={`bg-green-50 rounded-lg p-4 border border-green-100 ${!isExpanded ? "truncate h-24 overflow-hidden" : ""}`}>
              <h4 className="font-medium text-green-800 flex items-center mb-2">
                <FiThumbsUp className="mr-2" />
                Strengths
              </h4>
              <p className="text-gray-700">{feedback.strengths}</p>
            </div>
            <div className={`bg-blue-50 rounded-lg p-4 border border-blue-100 ${!isExpanded ? "truncate h-24 overflow-hidden" : ""}`}>
              <h4 className="font-medium text-blue-800 flex items-center mb-2">
                <FiTrendingUp className="mr-2" />
                Areas for Improvement
              </h4>
              <p className="text-gray-700">{feedback.areas_to_improve}</p>
            </div>
          </div>

          {isAcknowledged ? (
            <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
              <div className="flex items-center text-green-600 mb-2">
                <FiCheckCircle className="mr-2" />
                <span className="font-medium">Acknowledged on {new Date(feedback.acknowledged_at).toLocaleDateString()}</span>
              </div>
              {feedback.acknowledgment_comment && (
                <div className="bg-white p-3 rounded border-l-2 border-green-500 mt-2">
                  <p className="text-gray-700 italic">"{feedback.acknowledgment_comment}"</p>
                </div>
              )}
            </div>
          ) : isAcknowledging ? (
            <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
              <h4 className="font-medium text-gray-700 mb-2 flex items-center">
                <FiMessageSquare className="mr-2" />
                Add a comment (optional)
              </h4>
              <textarea
                className="w-full p-3 border rounded mb-3 focus:ring-2 focus:ring-indigo-200 focus:border-indigo-400"
                rows="3"
                placeholder="Share your thoughts about this feedback..."
                value={ackComment}
                onChange={(e) => setAckComment(e.target.value)}
              />
              <div className="flex justify-end space-x-2">
                <button
                  onClick={() => {
                    setAcknowledging(null);
                    setAckComment("");
                  }}
                  className="px-4 py-2 text-gray-600 border rounded-lg hover:bg-gray-100 transition"
                >
                  Cancel
                </button>
                <button
                  onClick={() => handleAcknowledge(feedback.id)}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition flex items-center"
                >
                  <FiCheckCircle className="mr-2" />
                  Confirm Acknowledgment
                </button>
              </div>
            </div>
          ) : (
            <div className="flex justify-between items-center">
              <button
                onClick={() => toggleExpandFeedback(feedback.id)}
                className="text-indigo-600 hover:text-indigo-800 flex items-center text-sm"
              >
                {isExpanded ? (
                  <>
                    <FiChevronUp className="mr-1" />
                    Show Less
                  </>
                ) : (
                  <>
                    <FiChevronDown className="mr-1" />
                    Read Full Feedback
                  </>
                )}
              </button>
              <button
                onClick={() => setAcknowledging(feedback.id)}
                className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition flex items-center"
              >
                <FiCheckCircle className="mr-2" />
                Acknowledge
              </button>
            </div>
          )}
        </div>
      </div>
    );
  };

  const EmptyState = () => (
    <div className="bg-white rounded-xl shadow-md p-8 text-center">
      <div className="text-gray-400 text-5xl mb-4">ðŸ“­</div>
      <h3 className="text-xl font-semibold text-gray-700 mb-2">
        No Feedback Received Yet
      </h3>
      <p className="text-gray-500 mb-6">
        You haven't received any feedback at this time.
      </p>
      <button
        onClick={() => navigate("/")}
        className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
      >
        Back to Dashboard
      </button>
    </div>
  );

  if (!isAuthorized) {
    return (
      <div className="min-h-screen bg-gray-50 p-4 md:p-8">
        <div className="max-w-4xl mx-auto">
          <UnauthorizedView />
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 p-4 md:p-8">
        <div className="max-w-4xl mx-auto">
          <LoadingSpinner />
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 p-4 md:p-8">
        <div className="max-w-4xl mx-auto">
          <ErrorDisplay />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-4 md:p-8">
      <div className="max-w-4xl mx-auto">
        <button
          onClick={() => navigate(-1)}
          className="flex items-center text-indigo-600 hover:text-indigo-800 mb-6 transition"
        >
          <FiArrowLeft className="mr-2" /> Back
        </button>

        {employeeInfo && <EmployeeHeader />}

        <div className="space-y-6">
          {feedbacks.length === 0 ? (
            <EmptyState />
          ) : (
            feedbacks.map((feedback) => (
              <FeedbackItem key={feedback.id} feedback={feedback} />
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default MyFeedback;
